function resetTheme()
{

    if (darkSwitch.checked) {
        $("#light-theme")[0].disabled = true;
        $("#dark-theme")[0].disabled = false;
    } else {
        $("#dark-theme")[0].disabled = true;
        $("#light-theme")[0].disabled = false;
    }
}

